                            <h5 style={{textAlign: 'center'}}><u>{a.version}</u></h5>
                            {a.version.mods.map(b => {
                                return(
                                    <li>{b.rewrite} </li>
                                )
                            })}
                            </div>
                        )
                    })}
                </ol>
            </div>
        )
    }

}

export default ModRewrites;
