import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Strapi from 'strapi-sdk-javascript/build/main';
import Loader from './Loader';
const apiUrl = process.env.API_URL || 'http://localhost:1337';
const strapi = new Strapi(apiUrl);

class Applications extends Component {
    state = {
        searchTerm: '',
        loadingItems: true,
        application: '',
        properties: [],
        vendor: '',
        vendorId: ''
    }

    async componentDidMount() {
        try {
            //console.log(this.props.match.params.itemId);
            const response = await strapi.request('POST', '/graphql', {
            data: {
                query: `query {
                    submission (id : "${this.props.match.params.submissionId}") {
                        file
                        received
                        processed
                        begin

                        vendor {
                        _id
                        name
                        logo {
                          _id
                          url
                        }
                      }
                        jurisdictions {
                        _id
                      }
                  }
                }`
            }
        }
        );
        this.setState({
            submission: response.data.submission.file,
            vendor: response.data.submission.vendor.name,
            jurisdictions: response.data.application.jurisdictions,
            loadingItems: false,
            vendorId: response.data.submission.vendor._id
        });
        }catch (err) {
            console.log(err);
            this.setState({ loadingItems: false });
        }
    }

    render() {
        let { submission, vendor, jurisdictions, loadingItems } = this.state;
        return(
            <div>
                <h1 style={{textAlign: 'center'}}>{application}</h1>
                <Link to={`/vendors/${vendorId}`}><h3 style={{textAlign: 'center'}}>{vendor}</h3></Link>
                <h3>
                    Properties that also utilize this app
                </h3>
                {jurisdictions.map(jurs => {
                    return(
                        <div>
                            <Link to={`/properties/${jurs._id}`}>{jurs.name}</Link>
                        </div>
                    );
                })} 
                {loadingItems && <Loader />}
            </div>
        );
    }
}

export default Applications;
