import React, { Component } from 'react';
import Test from './propstest';
import { Link } from 'react-router-dom';
import Strapi from 'strapi-sdk-javascript/build/main';
import Loader from './Loader';
import { FaRegEdit } from 'react-icons/fa';
import { pdfjs, Document, Page, View } from 'react-pdf';
import { Editor } from 'react-draft-wysiwyg';
import { EditorState, convertToRaw, ContentState } from 'draft-js';
import htmlToDraft from 'html-to-draftjs';
import draftToHtml from 'draftjs-to-html';
import Collapsible from 'react-collapsible';
import ModsList from './ModsList';
import { PieChart, Pie, Cell, ResponsiveContainer, Sector } from 'recharts';
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
const apiUrl = process.env.API_URL || 'http://localhost:1337';
const strapi = new Strapi(apiUrl);

let renderActiveShape = (props) => {
    let RADIAN = Math.PI / 180;
    let { cx, cy, midAngle, innerRadius, outerRadius, startAngle, endAngle,
        fill, payload, percent } = props;
    let sin = Math.sin(-RADIAN * midAngle);
    let cos = Math.cos(-RADIAN * midAngle);
    let sx = cx + (outerRadius + 10) * cos;
    let sy = cy + (outerRadius + 10) * sin;
    let mx = cx + (outerRadius + 30) * cos;
    let my = cy + (outerRadius + 30) * sin;
    let ex = mx + (cos >= 0 ? 1 : -1) * 22;
    let ey = my;
    let textAnchor = cos >= 0 ? 'start' : 'end';

    return (
      <g>
        <text x={cx} y={cy} dy={8} textAnchor="middle" fill={fill}>{payload.name}</text>
        <Sector
          cx={cx}
          cy={cy}
          innerRadius={innerRadius}
          outerRadius={outerRadius}
          startAngle={startAngle}
          endAngle={endAngle}
          fill={fill}
        />
        <Sector
          cx={cx}
          cy={cy}
          startAngle={startAngle}
          endAngle={endAngle}
          innerRadius={outerRadius + 6}
          outerRadius={outerRadius + 10}
          fill={fill}
        />
        <path d={`M${sx},${sy}L${mx},${my}L${ex},${ey}`} stroke={fill} fill="none"/>
        <circle cx={ex} cy={ey} r={2} fill={fill} stroke="none"/>
        <text x={ex + (cos >= 0 ? 1 : -1) * 12} y={ey} textAnchor={textAnchor} fill="#333">
          {`${payload.value}`}
        </text>
        <text x={ex + (cos >= 0 ? 1 : -1) * 12} y={ey} dy={18} textAnchor={textAnchor} fill="#999">
          {`${(percent * 100).toFixed(2)}%`}
        </text>
      </g>
    );
  };

class Submission extends Component {

    constructor(props) {
        super(props);
        let html = '<p>Testing 1234 😀</p>';
        let contentBlock = htmlToDraft(html);
        if (contentBlock) {
            let contentState = ContentState.createFromBlockArray(contentBlock.contentBlocks);
            let editorState = EditorState.createWithContent(contentState);
            this.state = {
                loadingItems: true,
                submission: '',
                jurisdictions: [],
                vendor: '',
                vendorId: '',
                numPages: null,
                pageNumber: 1,
                urns: [],
                mods: [],
                application: [],
                versions: [],
                html: '',
                editorState,
                uploadedImages: [],
                auditModsCount: 0,
                modsCount: 0,
                testableModsCount: 0,
                completeModsCount: 0,
                jiraModsCount: 0,
                revokedModsCount: 0,
                activeIndex: 0
            }
        }
    }

    async componentDidMount() {
        try {
            let response = await strapi.request('POST', '/graphql', {
            data: {
                query: `query {
                    submission (id : "${this.props.match.params.submissionId}") {
                        file
                        received
                        processed
                        begin
                        vendor {
                        _id
                        name
                        logo {
                          _id
                          url
                        }
                      }
                        jurisdictions {
                        _id
                        jurisdiction
                      }
                      urns {
                          _id
                          url
                      }
                      mods {
                          _id
                          modnumber
                          title
                          trackerid
                          moddetails
                          status
                          jira
                          testingzip {
                              _id
                              url
                          }
                      }
                      application {
                          _id
                          name
                      }
                      versions {
                          _id
                          version
                      }
                  }
                }`
            }
        }
        );
        this.setState({
            submission: response.data.submission.file,
            vendor: response.data.submission.vendor.name,
            jurisdictions: response.data.submission.jurisdictions,
            loadingItems: false,
            vendorId: response.data.submission.vendor._id,
            urns: response.data.submission.urns,
            mods: response.data.submission.mods,
            application: response.data.submission.application,
            versions: response.data.submission.versions,
            modsCount: response.data.submission.mods.length,
            auditModsCount: response.data.submission.mods.filter(prop => {
                return prop.status.toLowerCase().includes('audit'.toLowerCase())
            }).length,
            testableModsCount: response.data.submission.mods.filter(prop => {
                return prop.status.toLowerCase().includes('testable'.toLowerCase())
            }).length,
            completeModsCount: response.data.submission.mods.filter(prop => {
                return prop.status.toLowerCase().includes('complete'.toLowerCase())
            }).length,
            jiraModsCount: response.data.submission.mods.filter(prop => {
                return prop.status.toLowerCase().includes('jira'.toLowerCase())
            }).length,
            revokedModsCount: response.data.submission.mods.filter(prop => {
                return prop.status.toLowerCase().includes('revoked'.toLowerCase())
            }).length
        });
        console.log(this.state);
        }catch (err) {
            console.log(err);
            this.setState({ loadingItems: false });
        }
    }

    onEditorStateChange = (editorState) => {
        this.setState({
            editorState,
            html: draftToHtml(convertToRaw(editorState.getCurrentContent())).replace(/[\n]/g, '')
        });
    }

    renderMods() {
        let { mods } = this.state;
        mods = mods.sort((a, b) => (a.modnumber - b.modnumber));
        return (
        mods.map(x => {
        return (
            <div className="card" key={x._id} style={{marginTop: '10px', backgroundColor: '#eee'}}>
                <h5><Link to={`/updatemod/${x._id}`}><FaRegEdit /></Link></h5>
                <h5>Mod {x.modnumber}</h5>
                <h5 style={{color: '#007bff'}}>{x.title}</h5>
                <h5>{x.trackerid}</h5>
                <h5>Testing Status: {x.status}</h5>
                {x.jira != null &&
                    <h5>
                        <a href={x.jira} style={{backgroundColor: 'black', color: 'orange'}}>Jira</a>
                    </h5>
                }
                {x.testingzip != null &&
                    <h5>
                        <a href={`${apiUrl}${x.testingzip.url}`} style={{backgroundColor: 'black', color: 'hsl(128, 100%, 50%)'}}>Download Testing Performed</a>
                    </h5>
                }
                <Collapsible 
                transitionTime="250"
                trigger={this.renderDropDown4()}
                triggerWhenOpen={this.renderHide()}
                >
                    <Editor 
                    editorState={EditorState.createWithContent(ContentState.createFromBlockArray(htmlToDraft(x.moddetails.replace(/'''/g, '"')).contentBlocks))}
                    readOnly='true'
                    toolbarHidden='true'
                    />
                </Collapsible>
            </div>
            );}
        ))
    }

    renderAuditMods = () => {
        let { mods } = this.state;
        let auditMods = mods.filter(prop => {
            return prop.status.toLowerCase().includes('audit'.toLowerCase())
        })

        return (
            <div>
                {auditMods.map(x => {
                return (
                    <div className="card" key={x._id} style={{marginTop: '10px', backgroundColor: '#eee'}}>
                    <h5>Mod {x.modnumber}</h5>
                    <h5 style={{color: '#007bff'}}>{x.title}</h5>
                    <h5>{x.trackerid}</h5>
                    <h5>Testing Status: {x.status}</h5>
                    {x.jira != null &&
                        <h5>
                            <a href={x.jira}>Jira</a>
                        </h5>
                    }
                    {x.testingzip != null &&
                        <h5>
                            <a href={`${apiUrl}${x.testingzip.url}`} style={{backgroundColor: 'black', color: 'hsl(128, 100%, 50%)'}}>Download Testing Performed</a>
                        </h5>
                    }
                    <Collapsible 
                    transitionTime="250"
                    trigger={this.renderDropDown4()}
                    triggerWhenOpen={this.renderHide()}
                    >
                        <Editor 
                        editorState={EditorState.createWithContent(ContentState.createFromBlockArray(htmlToDraft(x.moddetails.replace(/'''/g, '"')).contentBlocks))}
                        readOnly='true'
                        toolbarHidden='true'
                        />
                    </Collapsible>
                </div>
                )
                })}
            </div>
        )
    }

    renderTestableMods = () => {
        let { mods } = this.state;
        let testableMods = mods.filter(prop => {
            return prop.status.toLowerCase().includes('testable'.toLowerCase())
        })
        return (
            <div>
                {testableMods.map(x => {
                return (
                    <div className="card" key={x._id} style={{marginTop: '10px', backgroundColor: '#eee'}}>
                    <h5>Mod {x.modnumber}</h5>
                    <h5 style={{color: '#007bff'}}>{x.title}</h5>
                    <h5>{x.trackerid}</h5>
                    <h5>Testing Status: {x.status}</h5>
                    {x.jira != null &&
                        <h5>
                            <a href={x.jira}>Jira</a>
                        </h5>
                    }
                    {x.testingzip != null &&
                        <h5>
                            <a href={`${apiUrl}${x.testingzip.url}`} style={{backgroundColor: 'black', color: 'hsl(128, 100%, 50%)'}}>Download Testing Performed</a>
                        </h5>
                    }
                    <Collapsible 
                    transitionTime="250"
                    trigger={this.renderDropDown4()}
                    triggerWhenOpen={this.renderHide()}
                    >
                        <Editor 
                        editorState={EditorState.createWithContent(ContentState.createFromBlockArray(htmlToDraft(x.moddetails.replace(/'''/g, '"')).contentBlocks))}
                        readOnly='true'
                        toolbarHidden='true'
                        />
                    </Collapsible>
                </div>
                )
                })}
            </div>
        )
    }

    renderJiraMods = () => {
        let { mods } = this.state;
        let jiraMods = mods.filter(prop => {
            return prop.status.toLowerCase().includes('jira'.toLowerCase())
        })
        return (
            <div>
                {jiraMods.map(x => {
                return (
                    <div className="card" key={x._id} style={{marginTop: '10px', backgroundColor: '#eee'}}>
                    <h5>Mod {x.modnumber}</h5>
                    <h5 style={{color: '#007bff'}}>{x.title}</h5>
                    <h5>{x.trackerid}</h5>
                    <h5>Testing Status: {x.status}</h5>
                    {x.jira != null &&
                        <h5>
                            <a href={x.jira}>Jira</a>
                        </h5>
                    }
                    {x.testingzip != null &&
                        <h5>
                            <a href={`${apiUrl}${x.testingzip.url}`} style={{backgroundColor: 'black', color: 'hsl(128, 100%, 50%)'}}>Download Testing Performed</a>
                        </h5>
                    }
                    <Collapsible 
                    transitionTime="250"
                    trigger={this.renderDropDown4()}
                    triggerWhenOpen={this.renderHide()}
                    >
                        <Editor 
                        editorState={EditorState.createWithContent(ContentState.createFromBlockArray(htmlToDraft(x.moddetails.replace(/'''/g, '"')).contentBlocks))}
                        readOnly='true'
                        toolbarHidden='true'
                        />
                    </Collapsible>
                </div>
                )
                })}
            </div>
        )
    }

    renderDropDown1() {
        return (
            <span className="trigger">
                Jurisdictions:
            </span>
        );
    }

    renderDropDown2() {
        return (
            <span className="trigger">
                URNs:
            </span>
        );
    }

    renderDropDown3() {
        let { modsCount } = this.state;
        return (
            <span className="trigger">
                Mods:  ({modsCount})
            </span>
        );
    }

    renderDropDown4() {
        return (
            <span className="trigger">
                Details:
            </span>
        );
    }

    renderDropDown5() {
        let { auditModsCount } = this.state;
        return (
            <span className="trigger">
                Audited Mods: ({auditModsCount})
            </span>
        );
    }

    renderDropDown6() {
        let { testableModsCount } = this.state;
        return (
            <span className="trigger">
                Testable Mods: ({testableModsCount})
            </span>
        );
    }

    renderDropDown7() {
        let { jiraModsCount } = this.state;
        return (
            <span className="trigger">
                Mods in JIRA:  ({jiraModsCount})
            </span>
        );
    }

    renderHide() {
        return (
            <span className="trigger">
                Hide
            </span>
        );
    }

