import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Strapi from 'strapi-sdk-javascript/build/main';
import Loader from './Loader';
import { pdfjs, Document, Page, View } from 'react-pdf';
import { Editor } from 'react-draft-wysiwyg';
import { EditorState, convertToRaw, ContentState } from 'draft-js';
import htmlToDraft from 'html-to-draftjs';
import draftToHtml from 'draftjs-to-html';
import Collapsible from 'react-collapsible';
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
const apiUrl = process.env.API_URL || 'http://nv-dt-534:1337';
const strapi = new Strapi(apiUrl);

class Submission extends Component {
    /*state = {
        loadingItems: true,
        submission: '',
        jurisdictions: [],
        vendor: '',
        vendorId: '',
        numPages: null,
        pageNumber: 1,
        urns: [],
        mods: []
    }*/

    constructor(props) {
        super(props);
        let html = '<p>Testing 1234 ðŸ˜€</p>';
        let contentBlock = htmlToDraft(html);
        if (contentBlock) {
            let contentState = ContentState.createFromBlockArray(contentBlock.contentBlocks);
            let editorState = EditorState.createWithContent(contentState);
            this.state = {
                loadingItems: true,
                submission: '',
                jurisdictions: [],
                vendor: '',
                vendorId: '',
                numPages: null,
                pageNumber: 1,
                urns: [],
                mods: [],
                application: [],
                versions: [],
                html: ''
            }
        }
    }

    async componentDidMount() {
        try {
            const response = await strapi.request('POST', '/graphql', {
            data: {
                query: `query {
                    submission (id : "${this.props.match.params.submissionId}") {
                        file
                        received
                        processed
                        begin
                        vendor {
                        _id
                        name
                        logo {
                          _id
                          url
                        }
                      }
                        jurisdictions {
                        _id
                        jurisdiction
                      }
                      urns {
                          _id
                          url
                      }
                      mods {
                          _id
                          title
                          trackerid
                          moddetails
                      }
                      application {
                          _id
                          name
                      }
                      versions {
                          _id
                          version
                      }
                  }
                }`
            }
        }
        );
        this.setState({
            submission: response.data.submission.file,
            vendor: response.data.submission.vendor.name,
            jurisdictions: response.data.submission.jurisdictions,
            loadingItems: false,
            vendorId: response.data.submission.vendor._id,
            urns: response.data.submission.urns,
            mods: response.data.submission.mods,
            application: response.data.submission.application,
            versions: response.data.submission.versions
        });
        console.log(this.state);
        }catch (err) {
            console.log(err);
            this.setState({ loadingItems: false });
        }
    }

    onEditorStateChange = (editorState) => {
        this.setState({
            editorState,
            html: draftToHtml(convertToRaw(editorState.getCurrentContent())).replace(/[\n]/g, '')
        });
    }

    renderMods() {
        let { mods } = this.state;
        return (
            <div>
                {mods.map(a => {
                    return (
                        <p>
                            <strong>{a.title} {a.trackerid}</strong><br />
                            {a.moddetails}
                        </p>
                    )
                })}
            </div>
        );
    }

    renderDropDown1() {
        return (
            <span className="trigger">
                Jurisdictions:
            </span>
        );
    }

    renderDropDown2() {
        return (
            <span className="trigger">
                URNs:
            </span>
        );
    }

    renderHide() {
        return (
            <span className="trigger">
                Hide
            </span>
        );
    }

    onDocumentLoadSuccess = ({ numPages }) => {
        this.setState({ numPages });
      }

      changePage = offset => this.setState(prevState => ({
        pageNumber: prevState.pageNumber + offset,
      }));
    
      previousPage = () => this.changePage(-1);
    
      nextPage = () => this.changePage(1);
    
    render() {
        let { submission, vendor, jurisdictions, loadingItems, vendorId, numPages, pageNumber, urns, mods, application, versions } = this.state;
        return(
            <div>
                <h1 style={{textAlign: 'center'}}>{submission}</h1>
                <Link to={`/manufacturer/${vendorId}`}><h3 style={{textAlign: 'center'}}>{vendor}</h3></Link><br />
                <h1 style={{textAlign: 'center'}}>{application.name} {versions.map(y => {
                    return (
                        <span>{y.version}</span>
                    )
                })}</h1>
                <Collapsible 
                transitionTime="250" 
                trigger={this.renderDropDown1()}
                triggerWhenOpen={this.renderHide()}
                >
                {jurisdictions.map(jurs => {
                    return(
                        <div>
                           <Link to={`/jurisdiction/${jurs._id}`}>{jurs.jurisdiction}</Link>
                        </div>
                    );
                })}
                </Collapsible>
                <Collapsible 
                transitionTime="250" 
                trigger={this.renderDropDown2()}
                triggerWhenOpen={this.renderHide()}
                >
                {urns.map(a => {
                    return (
                        <div>
                            <Document
                            file={a.url}
                            onLoadSuccess={this.onDocumentLoadSuccess}
                            onItemClick={this.nextPage}
                            >
                                <Page pageNumber={pageNumber} style={{marginRight: 'auto'}}/>
                            </Document>
                            <p>Page {pageNumber || (numPages ? 1 : '--')} of {numPages || '--'}</p>
                            <button
                            type="button"
                            disabled={pageNumber <= 1}
                            onClick={this.previousPage}
                            >
                                Back
                            </button>
                            <button
                            type="button"
                            disabled={pageNumber >= numPages}
                            onClick={this.nextPage}
                            >
                                Next
                            </button>
                        </div>
                    )
                })}
                </Collapsible>
                <div>
                    <h1>Mods:{'\u00A0'}</h1>
                    {mods.map(x => {
                        return (
                            <p>
                                Tracker: {x.trackerid} <br />
                                Title: {x.title}
                            </p>
                        )
                    })}
                </div>
                {loadingItems && <Loader />}
            </div>
        );
    }
}

export default Submission;
