import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Strapi from 'strapi-sdk-javascript/build/main';
import Loader from './Loader';
const apiUrl = process.env.API_URL || 'http://192.168.0.178:1337';
const strapi = new Strapi(apiUrl);

class Jurisdiction extends Component {
    state = {
        loadingItems: true,
        jurisdictions: [],
        submissions: []
    }

    async componentDidMount() {
        try {
            //console.log(this.props.match.params.itemId);
            const response = await strapi.request('POST', '/graphql', {
            data: {
                query: `query {
                    jurisdictions {
                        _id
                        jurisdiction
                        submissions {
                          _id
                          file
                        }
                  }
                }`
            }
        }
        );
        this.setState({
            jurisdictions: response.data.jurisdictions,
            loadingItems: false
        });
        }catch (err) {
            console.log(err);
            this.setState({ loadingItems: false });
        }
    }

    renderJurisdicions = () => {
        let { jurisdictions, loadingItems } = this.state;
        return (
            <div>
                {jurisdictions.submissions.map(a => {
                    return (
                        <p>{a.file}</p>
                    )
                })}
                {loadingItems && <Loader />}
            </div>
        );
    }

    /*renderSubmissions = () => {
        let { jurisdictions, loadingItems } = this.state;
        return (
            <div>
                {jurisdictions.map(a => {
                <h1>Jurisdiction</h1> <br />
                <h4>{a.jurisdiction}</h4> <br />
                <h3>Submissions for {a.jurisdiction}</h3>
                })}
                {loadingItems && <Loader />}
            </div>
        );
    }*/

    render() {
        return(
            <div style={{textAlign: 'center'}}>
                {renderSubmissions()}
            </div>
        );
    }
}

export default Jurisdiction;
