import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import Strapi from 'strapi-sdk-javascript/build/main';
import Loader from './Loader';
import { FaRegEdit } from 'react-icons/fa';
import { EditorState, convertToRaw, ContentState } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import htmlToDraft from 'html-to-draftjs';
import draftToHtml from 'draftjs-to-html';
import Collapsible from 'react-collapsible';
const apiUrl = process.env.API_URL || 'http://localhost:1337';
const strapi = new Strapi(apiUrl);

class ModList extends Component {

    constructor(props) {
        super(props);
        let html = '<p>Testing 1234 ðŸ˜€</p>';
        let contentBlock = htmlToDraft(html);
        if (contentBlock) {
            let contentState = ContentState.createFromBlockArray(contentBlock.contentBlocks);
            let editorState = EditorState.createWithContent(contentState);
            this.state = {
                loadingItems: true,
                submission: '',
                mods: [],
                application: [],
                html: '',
                editorState,
                auditModsCount: 0,
                modsCount: 0,
                testableModsCount: 0,
                completeModsCount: 0,
                jiraModsCount: 0,
                revokedModsCount: 0
            }
        }
    }

    async componentDidMount() {
        try {
            let response = await strapi.request('POST', '/graphql', {
            data: {
                query: `query {
                    submission (id : "${this.props.submissionId}") {
                        file
                        received
                        processed
                        begin
                        jurisdictions {
                        _id
                        jurisdiction
                      }
                      mods {
                        _id
                        modnumber
                        title
                        trackerid
                        moddetails
                        status
                        jira
                        testingzip {
                            _id
                            url
                        }
                    }
                      versions {
                          _id
                          version
                          mods {
                            _id
                            modnumber
                            title
                            trackerid
                            moddetails
                            status
                            jira
                            testingzip {
                                _id
                                url
                            }
                        }
                      }
                      application {
                          _id
                          name
                      }
                  }
                }`
            }
        }
        );
        let getMods = response.data.submission.versions.map(a => {return a.mods});
        for (let i = 0; i < getMods.length; i++){
            let ModsList = getMods[i].push(getMods[i].mods);
            console.log(ModsList);
        }

        this.setState({
            submission: response.data.submission.file,
